import {Component} from '@angular/core';
import {FormBuilder, FormGroup, Validators} from '@angular/forms';
import {HttpClient} from '@angular/common/http';

@Component({
    selector: 'app-registration-form',
    templateUrl:'./registration-form.component.html',
    styleUrls:['registration-form.component.css']
})

export class RegistrationFormComponent{
    registrationForm: FormGroup;
    locations:string[]=['Tamilnadu','Andhrapradesh'];
    colleges:string[]=['SVCE','VIT Hyderabad'];


constructor(private fb:FormBuilder, private http:HttpClient){
    this.registrationForm=this.fb.group({
        name:['', Validators.required],
        gender:['', Validators.required],
        dob:['', Validators.required],
        email:['', Validators.required],
        location:['',Validators.required],
        college:['', Validators.required],

    });
}
ngOnInit(){
    this.registrationForm.get('location')?.valueChanges.subscribe((location)=>{
        if(location) {
            this.fetchColleges(location);
        }
    });
}
fetchColleges(location:string)
{
    const pt='http://localhost:8080/api/colleges?location=${location}';
    this.http.get<string[]>(pt).subscribe((colleges)=>{
        this.colleges=colleges;
    });
}

submitForm(){
    alert("Form submitted successfully!");
}
}
